sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
   var size = g_form.getValue('size');
   var other_size = g_form.getValue('other_size');

		// Clear all of the choices from the What needed field choice list
		g_form.clearOptions('size');
		g_form.clearOptions('other_size');
		// If the value of the Request type field is hr, add
		// two hr choices and other to the What needed field choice list
		if(newValue == 'red'){
			g_form.addOption('size','','-- None --');
			g_form.addOption('size','s','Small');
			g_form.addOption('size','m','Medium');
			g_form.addOption('size','l','Large');
		}

		if(newValue == 'black'){
			g_form.addOption('size','','-- None --');
			g_form.addOption('size','s','Small');
			g_form.addOption('size','m','Medium');
			g_form.addOption('size','xl','Extra Large');
		}

		if(newValue == 'blue'){
			g_form.addOption('size','','-- None --');
			g_form.addOption('size','s','Small');
			g_form.addOption('size','xxl','Double Extra Large');
		}

		if(newValue == 'yellow'){
			g_form.addOption('size','','-- None --');
			g_form.addOption('size','m','Medium');
			g_form.addOption('size','l','Large');
		}

		if(newValue == 'other'){
			setTimeout(function(){
				g_form.addOption('other_size','','-- None --');
				g_form.addOption('other_size','s','Small');
				g_form.addOption('other_size','m','Medium');
				g_form.addOption('other_size','l','Large');
				g_form.addOption('other_size','xl','Extra Large');
				g_form.addOption('other_size','xxl','Double Extra Large');
			}, 1000);

		}

		// If the form is loading and it is not a new record, set the u_what_needed value to the
		// value from the record before it was loaded
		if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('size', size);
			g_form.setValue('other_size', other_size);

		}

}
